import { observable, action } from 'mobx'

const state = observable({ 
  collapsed: false,
  expand: false
})
const actions = {}

actions.showToggle = action((show)=>{
  state.expand = show
})

actions.updateKeys = action((keys)=>{
  state.openKeys = keys
})

export default { state, actions }
